tinymce.PluginManager.add('jbimages',function(editor,url){function jbBox(){editor.windowManager.open({title:'Nahrát obrázek',file:url+'/dialog-v4.htm',width:350,height:135,buttons:[{text:'Upload',classes:'widget btn primary first abs-layout-item',disabled:true,onclick:'close'},{text:'Close',onclick:'close'}]})}editor.addButton('jbimages',{tooltip:'Nahrát obrázek',icon:'image',text:'Vložit',onclick:jbBox});editor.addMenuItem('jbimages',{text:'Upload image',icon:'image',context:'insert',onclick:jbBox})});